declare const _default: "\n    #ifdef SSAO\n        #define SSAO_TEXTURE\n    #endif\n\n    #if DEBUG_COMPOSE == ssao\n        #define SSAO_TEXTURE\n    #endif\n\n    #ifdef SSAO_TEXTURE\n        uniform sampler2D ssaoTexture;\n        \n        // Global variable for debug\n        float dSsao;\n        \n        vec3 applySsao(vec3 color, vec2 uv) {\n            dSsao = texture2DLod(ssaoTexture, uv, 0.0).r;\n            \n            #ifdef SSAO\n                return color * dSsao;\n            #else\n                return color;\n            #endif\n        }\n    #endif\n";
export default _default;
